import React from 'react';
import * as THREE from 'three';
import MetalSphere from '../Components/3D/MetalSphere.three';
import Loading from './../Components/Loading.component';
import { handleSceneResize, initEventListener } from './../Utils/sceneResize';
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls.js";


export default class MainScene extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            loading: this.props.loading ? true : false,
        }
        //3D Scene components
        this.divRef = React.createRef();
        this.renderer = null;
        this.camera = null;
        this.scene = null;
    }

    componentDidMount() {
        this.initScene();
    }
    initScene = () => {
        //init Loading Manager
        const loadingManager = new THREE.LoadingManager();
        loadingManager.onLoad = () => {
            this.setState({ loading: false }, () => { })
        }

        //init Renderer
        this.renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
        this.renderer.setPixelRatio(window.devicePixelRatio);
        this.renderer.autoClear = false;
        this.renderer.setClearColor(new THREE.Color(0x222222));
        this.renderer.setSize(this.divRef.current.offsetWidth, this.divRef.current.offsetHeight);
        this.divRef.current.appendChild(this.renderer.domElement);

        //init Scene
        this.scene = new THREE.Scene();
        this.scene.background = new THREE.Color(0x222222);

        //init Camera
        this.camera = new THREE.PerspectiveCamera(7, this.divRef.current.offsetWidth / this.divRef.current.offsetHeight, 0.1, 1000);
        this.camera.position.x = 30;
        this.controls = new OrbitControls(this.camera, this.renderer.domElement);
        //Metal Sphere
        let sphere = new MetalSphere().create(loadingManager);
        this.scene.add(sphere);

        //Ambient Light
        let ambientLight = new THREE.AmbientLight(0xFFFFFF);
        ambientLight.intensity = 1;
        this.scene.add(ambientLight);
        let hemiLight = new THREE.HemisphereLight(0xffeeb1, 0x080820, 4);
        this.scene.add(hemiLight);

        //Directional Light
        let directionalLight = new THREE.DirectionalLight(0xe8f7ff, 4);
        directionalLight.position.set(5, 15, 70);
        this.scene.add(directionalLight);

        //SpotLight
        let light = new THREE.SpotLight(0xffa95c,4);
        light.position.set(-50,50,50);
        light.castShadow = true;
        this.scene.add( light );

        //pin
        let mesh = new THREE.Mesh(
            new THREE.SphereBufferGeometry(0.1, 30, 30),
            new THREE.MeshBasicMaterial({color:0xff0000})
        )
        let mesh1 = new THREE.Mesh(
            new THREE.SphereBufferGeometry(0.1, 30, 30),
            new THREE.MeshBasicMaterial({color:0x00ff00})
        )

        function Conversion(p){
            let lat = (90-p.lat)*Math.PI/180;
            let lng = (p.lng+180)*Math.PI/180;

            let x = -(Math.sin(lat)*Math.cos(lng))
            let y = (Math.sin(lat)*Math.sin(lng))
            let z = (Math.cos(lat))

            return{
                x, y, z
            }
        }

        let point1 = {
            lat:35.6897,
            lng:139.6922
        }
        let point2 = {
            lat:18.9667,
            lng:72.8333
        }

        let pos = Conversion(point1);
        let pos1 = Conversion(point2);
        mesh.position.set(pos.x, pos.y, pos.z)
        this.scene.add(mesh)
        mesh1.position.set(pos1.x,pos1.y,pos1.z)
        this.scene.add(mesh1)
        this.ConnectingLines(pos,pos1)


        //init Handle Resize
        handleSceneResize(window, this.camera, this.renderer)
        initEventListener(window);

        //Animation Loop
        const animate = () => {
            requestAnimationFrame(animate);
            this.renderer.render(this.scene, this.camera);
            // if (sphere,mesh) {
            //     let rotationRad = THREE.MathUtils.degToRad(.3);
            //     sphere.rotateY(rotationRad);
            //     mesh.rotateY(rotationRad)

            // }
            light.position.set( 
                this.camera.position.x + 10,
                this.camera.position.y + 10,
                this.camera.position.z + 10,
              );
              light.shadow.bias = -0.0001;
              light.shadow.mapSize.width = 1024*4;
              light.shadow.mapSize.height = 1024*4;
        }
        animate();
    }
    ConnectingLines(p1,p2){
        let v1 = new THREE.Vector3(p1.x, p1.y, p1.z)
        let v2 = new THREE.Vector3(p2.x, p2.y, p2.z)
        let points = []
        for (let i = 0; i <=20; i++) {
            let p = new THREE.Vector3().lerpVectors(v1, v2, i/20)
            p.normalize()
            p.multiplyScalar(1 + 0.08*Math.sin(Math.PI*i/20)) 
            points.push(p)             
        };
        let path = new THREE.CatmullRomCurve3(points)
        const geometry = new THREE.TubeGeometry( path, 27, 0.01, 2, false );
        const material = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );
        const mesh = new THREE.Mesh( geometry, material );
        this.scene.add( mesh );
    }


    render() {
        return (<>
            <div className="three-container" ref={this.divRef}></div>
            {this.state.loading ? <Loading /> : ''}
        </>)

    }
}
